mapper.select:
select companyName,logoImage from room r
inner join host h on r.hostNum = h.num
where r.Num = #{num}

select  r.num num,name,r.address address,headcount,weekAmPrice,payment from room r
inner join host h on r.hostNum = h.num
where r.hostNum = #{hostNum}  order by ${sort}

select r.name,question,answer,status,q.writeday,answerday from QnA q
inner join room r on q.roomNum= r.num
where q.userNum=#{userNum} ${sort}
