mapper.update:
UPDATE bookingDetail
set bookingStatus=2
WHERE num = #{num}

mapper.select:
select b.num, b.totalPrice, b.pg, b.createdAt, b.payStatus, b.bookingDetailnum, r.num as roomNum, r.name as roomName, r.hostNum, bd.name, bd.bookingStatus, b.merchantUid from booking b inner join room r on b.roomNum = r.num inner join bookingDetail bd on b.bookingDetailNum = bd.num inner join host h on r.hostNum = h.num where bd.bookingStatus=4 and (b.payStatus=1 or b.payStatus=0) and b.createdAt >= #{sdate} and b.createdAt <= #{edate}
and r.name = #{roomName}
and r.hostNum = #{hostNum} and b.payStatus = #{payStatus}
